Function Button_Click()
{
    $label3.Text = "Installing, please wait.."
    $uri = 'https://' + $serverHost + '/download/12.0.0.365/winnt/N-central/WindowsAgentSetup.exe'
    Invoke-Webrequest -uri $uri -OutFile "C:\temp\WindowsAgentSetup.exe"
    $selectedC = $comboBox2.SelectedItem.ToString()
    $selectedID = $customers | where-object {$_.Name -like "$SelectedC"} | Select ID 
    $selectedIDF = $selectedID.id
    $Parms = '/s /v" /qn SERVERPROTOCOL=HTTPS SERVERADDRESS=' + $serverHost + 'SERVERPORT=443 CUSTOMERID=' + $selectedIDF + ' ' + 'CUSTOMERNAME=\"' + $selectedC + '\"'
    Start-Process  C:\temp\WindowsAgentSetup.exe -ArgumentList $Parms -Wait
    $label3.Text = "Installation Completed!"
    }

